from neo4j import GraphDatabaseclass Neo4jConnector:    def __init__(self, pubtrends_config, connect=True):        self.config = pubtrends_config        self.neo4jdriver = None        if not connect:            return        if Neo4jConnector.neo4j_configured(pubtrends_config):            try:                self.neo4jdriver = GraphDatabase.driver(                    f'bolt://{pubtrends_config.neo4j_host}:{pubtrends_config.neo4j_port}',                    auth=(pubtrends_config.neo4j_username, pubtrends_config.neo4j_password)                )                with self.neo4jdriver.session() as session:                    session.run('Match () Return 1 Limit 1')            except Exception:                raise Exception('Failed to connect to database.')    def close_connection(self):        if self.neo4jdriver:            self.neo4jdriver.close()    @staticmethod    def neo4j_configured(pubtrends_config):        return pubtrends_config.neo4j_host and \            pubtrends_config.neo4j_port and \            pubtrends_config.neo4j_username and \            pubtrends_config.neo4j_password